    grid = [list(stdin.readline().strip()) for _ in range(n)]

    def is_valid(x: int, y: int) -> bool:
        if grid[x][y] == '.':
            return True
        return False

    for i in range(n):
        for j in range(m):
            if is_valid(i, j):
                grid[i][j] = 'E'
                if i > 0 and is_valid(i - 1, j) and grid[i - 1][j] != 'E':
                    grid[i - 1][j] = 'E'
                if j > 0 and is_valid(i, j - 1) and grid[i][j - 1] != 'E':
                    grid[i][j - 1] = 'E'
                if i < n - 1 and is_valid(i + 1, j) and grid[i + 1][j] != 'E':
                    grid[i + 1][j] = 'E'
                if j < m - 1 and is_valid(i, j + 1) and grid[i][j + 1] != 'E':
                    grid[i][j + 1] = 'E'

    return '\n'.join([''.join(row) for row in grid])

