def solution(stdin: str) -> str:
    def find_empty_drawer(drawer1, drawer2, visited, items):
        if drawer1 not in visited:
            return drawer1
        if drawer2 not in visited:
            return drawer2
        return None

    def find_item(drawer, items):
        for i, (a, b) in enumerate(items):
            if drawer == a or drawer == b:
                return i
        return None

    def move_item(drawer, items):
        item_index = find_item(drawer, items)
        if item_index is not None:
            a, b = items[item_index]
            if a != drawer:
                return b
            else:
                return a
        return None

    def store_item(item_index, drawer, items, result):
        a, b = items[item_index]
        if a == drawer:
            result[item_index] = "LADICA"
        else:
            result[item_index] = "SMECE"

    def process_items(items, result):
        visited = set()
        for i, (a, b) in enumerate(items):
            if result[i] != "LADICA":
                drawer1 = find_empty_drawer(a, b, visited, items)
                if drawer1 is not None:
                    store_item(i, drawer1, items, result)
                    visited.add(drawer1)
                else:
                    drawer2 = move_item(a, items)
                    if drawer2 is not None:
                        store_item(i, drawer2, items, result)
                        visited.add(drawer2)
                    else:
                        result[i] = "SMECE"
            visited.add(a)
            visited.add(b)

    lines = stdin.split("\n")
    N, L = map(int, lines[0].split())
    items = []
    result = [""] * N
    for i in range(1, N + 1):
        a, b = map(int, lines[i].split())
        items.append((a, b))
    process_items(items, result)
    return "\n".join(result)

