        return "impossible"
    if n == 2:
        return "impossible" if temps[0] == temps[1] else " ".join(map(str, sorted(temps, reverse=True)))
    max_pool = temps.index(max(temps))
    if max_pool == 0 or max_pool == n - 1:
        return "impossible"
    return " ".join(map(str, [temps[max_pool - 1], temps[max_pool], temps[max_pool + 1]]))

