def solution(stdin: str) -> str:
    # Split the input into lines and convert them to lists of course numbers
    lines = stdin.split('\n')
    courses = [list(map(int, line.split())) for line in lines[1:]]

    # Create a dictionary to store the popularity of each combination
    popularity = {}

    # Iterate through the combinations of courses
    for i, course in enumerate(courses):
        # Sort the courses and convert them to a tuple for use as a dictionary key
        sorted_courses = tuple(sorted(course))

        # Increment the popularity of this combination
        if sorted_courses in popularity:
            popularity[sorted_courses] += 1
        else:
            popularity[sorted_courses] = 1

    # Find the most popular combination
    most_popular = max(popularity, key=popularity.get)

    # Count the number of frosh taking the most popular combination
    count = popularity[most_popular]

    return str(count)

