def solution(stdin: str) -> str:
    def build_table(key_phrase: str) -> list:
        table = [['' for _ in range(5)] for _ in range(5)]
        key_phrase = key_phrase.replace(" ", "").replace("q", "").upper()
        unique_chars = set(key_phrase)

        for i, char in enumerate(unique_chars):
            row = i // 5
            col = i % 5
            table[row][col] = char

        for char in "ABCDEFGHIJKLMNOPQRSTUVWXYZ":
            if char not in unique_chars:
                for row in table:
                    if '' in row:
                        row[row.index('')] = char
                        break

        return table

    def encrypt(table: list, plaintext: str) -> str:
        plaintext = plaintext.replace(" ", "").upper()
        ciphertext = ""

        for i in range(0, len(plaintext), 2):
            pair = plaintext[i:i + 2]

            if len(pair) == 1:
                pair += "X"

            if pair[0] == pair[1]:
                pair = pair[0] + "X" + pair[1]

            row1, col1 = get_position(table, pair[0])
            row2, col2 = get_position(table, pair[1])

            if row1 == row2:
                ciphertext += table[row1][(col1 + 1) % 5] + table[row2][(col2 + 1) % 5]
            elif col1 == col2:
                ciphertext += table[(row1 + 1) % 5][col1] + table[(row2 + 1) % 5][col2]
            else:
                ciphertext += table[row1][col2] + table[row2][col1]

        return ciphertext.upper()

    def get_position(table: list, char: str) -> tuple:
        for i in range(5):
            for j in range(5):
                if table[i][j] == char:
                    return i, j

    key_phrase, plaintext = stdin.split('\n')
    table = build_table(key_phrase)
    ciphertext = encrypt(table, plaintext)

    return ciphertext

