def solution(stdin: str) -> str:
    N, M = map(int, stdin.readline().split())
    grid = []
    for i in range(N):
        grid.append(list(stdin.readline().strip()))

    while True:
        next_grid = [row.copy() for row in grid]
        changed = False

        for i in range(1, N - 1):
            for j in range(M):
                if grid[i][j] == "V" and grid[i - 1][j] == ".":
                    next_grid[i - 1][j] = "V"
                    changed = True
                elif grid[i][j] == "V" and grid[i - 1][j] == "#":
                    if j > 0 and grid[i][j - 1] == ".":
                        next_grid[i][j - 1] = "V"
                        changed = True
                    if j < M - 1 and grid[i][j + 1] == ".":
                        next_grid[i][j + 1] = "V"
                        changed = True

        grid = [row.copy() for row in next_grid]

        if not changed:
            break

    return "\n".join(["".join(row) for row in grid])

