    def hailstone_sequence(n: int) -> list:
        if n == 1:
            return [1]
        elif n % 2 == 0:
            return [n] + hailstone_sequence(n // 2)
        else:
            return [n] + hailstone_sequence(3 * n + 1)

    hailstone = hailstone_sequence(n)
    return str(sum(hailstone))

def solution(stdin: str) -> str:
    n = int(stdin.split('\n')[0])
    rooms = list(map(int, stdin.split('\n')[1].split()))
    total_exams = sum(rooms)

    if total_exams % 2 != 0:
        return "impossible"

    mid = total_exams // 2

    for i in range(n):
        if rooms[i] > mid:
            return "impossible"

    safe_order = sorted(range(n), key=lambda k: rooms[k], reverse=True)

    pile = [0] * mid
    for room in safe_order:
        for i in range(rooms[room]):
            if pile:
                pile.pop()
            else:
                return "impossible"

    return " ".join(map(str, safe_order + [i for i in range(n) if i not in safe_order]))

def solution(stdin: str) -> str:
    def find_last_part(parts_count: int, days: int, parts: list) -> int:
        distinct_parts = set(parts)
        if len(distinct_parts) < parts_count:
            return "paradox avoided"
        for i in range(days - 1, -1, -1):
            if parts[i] not in distinct_parts:
                distinct_parts.remove(parts[i])
                if len(distinct_parts) == parts_count:
                    return i + 1
        return "paradox avoided"

    stdin_list = stdin.split('\n')
    parts_count, days = map(int, stdin_list[0].split())
    parts = stdin_list[1:]

    return str(find_last_part(parts_count, days, parts))

