    def find_cycle(i: int, friends: list, visited: set) -> list:
        cycle = [i]
        while True:
            visited.add(i)
            i = friends[i]
            cycle.append(i)
            if i in visited:
                cycle.pop()
                break
            if i == 0:
                break
        return cycle

    def find_gift_cycle(friends: list, visited: set) -> list:
        for i in range(1, len(friends)):
            if i not in visited:
                cycle = find_cycle(i, friends, visited)
                if cycle:
                    return cycle
        return []

    def fill_cycle(cycle: list, friends: list) -> None:
        n = len(cycle)
        for i in range(n):
            j = (i + 1) % n
            friends[cycle[i]] = cycle[j]

    n = int(stdin.readline().strip())
    friends = [0] + list(map(int, stdin.readline().strip().split()))
    visited = set()

    cycle = find_gift_cycle(friends, visited)
    while cycle:
        fill_cycle(cycle, friends)
        visited = set()
        cycle = find_gift_cycle(friends, visited)

    output = [0] * n
    for i in range(1, n):
        output[i] = friends[i]

    return " ".join(map(str, output))

