        parent = [i for i in range(n)]
        rank = [0] * n
        mst = []
        total_edges = 0

        edges.sort(key=lambda x: x[2])

        for edge in edges:
            if union(parent, rank, edge[0] - 1, edge[1] - 1):
                mst.append(edge)
                total_edges += 1
                if total_edges == n - 1:
                    break

        return mst

