        stack = []
        for bracket in brackets:
            if bracket == "(":
                stack.append(bracket)
            elif bracket == ")":
                if not stack:
                    return False
                stack.pop()
        return not stack

    def generate_regular(length: int, prefix: str) -> int:
        if length == 0:
            return int(is_regular(prefix))
        count = 0
        for i in range(length):
            new_prefix = prefix + "(" + i * "1+"
            count += generate_regular(length - i - 1, new_prefix)
        return count

    modulo = 1000000007
    result = generate_regular(n, "")
    for i in range(1, n + 1):
        result += generate_regular(n - i, "") * (2 ** (i - 1) * (2 ** (2 * i - 1)) * (2 ** (n - i) - 1))
        result %= modulo

    for i in range(len(s) // 2):
        result *= 2
        result %= modulo

    return str(result)

