    x_coords = sorted(map(int, stdin.split()[4:4 + N]))
    y_coords = sorted(map(int, stdin.split()[4 + N:]))

    def binary_search(arr, target):
        left, right = 0, len(arr) - 1
        while left <= right:
            mid = (left + right) // 2
            if arr[mid] == target:
                return True
            elif arr[mid] < target:
                left = mid + 1
            else:
                right = mid - 1
        return False

    for Z in range(X + 1, Y):
        if all(x < Z <= y for x, y in zip(x_coords, y_coords)) and not binary_search(x_coords, Z) and not binary_search(y_coords, Z):
