    matrix = [list(map(int, stdin.readline().split())) for _ in range(n)]

    def is_good_matrix(matrix):
        for i in range(n):
            for j in range(m):
                if matrix[i][j] != (i * m + j + 1):
                    return False
        return True

    def count_cycles(matrix, column):
        cycles = 0
        temp = matrix[0][column]
        for i in range(n):
            if matrix[i][column] != temp:
                cycles += 1
            temp = matrix[i][column]
        return cycles

    def update_matrix(matrix, row, column, value):
        matrix[row][column] = value
        for i in range(n):
            matrix[i][column] = (matrix[i][column] + value - 1) % (n * m) + 1

    def count_moves():
        moves = 0
        for i in range(n):
            for j in range(m):
                if matrix[i][j] != (i * m + j + 1):
                    value = (i * m + j + 1)
                    update_matrix(matrix, i, j, value)
                    moves += 1
        for j in range(m):
            cycles = count_cycles(matrix, j)
            moves += cycles
        return moves

    if is_good_matrix(matrix):
        return str(0)
    else:
        return str(count_moves())

