    n, m = map(int, lines[0].split())
    a = list(map(int, lines[1].split()))
    offers = [list(map(int, lines[i].split())) for i in range(2, 2 + m)]

    def kruskal(n, a, offers):
        parent = list(range(n))
        rank = [0] * n

        def find(x):
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            xroot = find(x)
            yroot = find(y)
            if xroot == yroot:
                return False
            if rank[xroot] < rank[yroot]:
                parent[xroot] = yroot
            elif rank[xroot] > rank[yroot]:
                parent[yroot] = xroot
            else:
                parent[yroot] = xroot
                rank[xroot] += 1
            return True

        edges = []
        for x, y, w in offers:
            edges.append((w, x - 1, y - 1))
        for i in range(n):
            for j in range(i + 1, n):
                edges.append((a[i] + a[j], i, j))

        edges.sort()

        connected_components = n
        minimum_cost = 0
        for w, x, y in edges:
            if union(x, y):
                connected_components -= 1
                minimum_cost += w
                if connected_components == 1:
                    return minimum_cost
        return None

    return str(kruskal(n, a, offers))

