    def find(parent, u):
        if parent[u] == u:
            return u
        parent[u] = find(parent, parent[u])
        return parent[u]

    def union(parent, rank, u, v):
        u_root = find(parent, u)
        v_root = find(parent, v)

        if u_root == v_root:
            return False

        if rank[u_root] > rank[v_root]:
            parent[v_root] = u_root
        elif rank[u_root] < rank[v_root]:
            parent[u_root] = v_root
        else:
            parent[v_root] = u_root
            rank[u_root] += 1

        return True

    def kruskal(n, edges):
        parent = [i for i in range(n)]
        rank = [0] * n
        mst_cost = 0
        mst_edges = []

        for e in sorted(edges, key=lambda x: x[2]):
            u, v, w = e
            if union(parent, rank, u - 1, v - 1):
                mst_cost += w
                mst_edges.append(e)

        return mst_cost, mst_edges

    def unify_mst(n, edges):
        mst_cost, mst_edges = kruskal(n, edges)
        modified_edges = []

        for i, e in enumerate(mst_edges):
            u, v, w = e
            for new_w in range(w + 1, 10**9 + 1):
                edges[i][2] = new_w
                new_mst_cost, new_mst_edges = kruskal(n, edges)

                if new_mst_cost != mst_cost:
                    edges[i][2] = w
                    break

                modified_edges.append((i, new_w - w))

        return modified_edges

    lines = stdin.split('\n')
    n, m = map(int, lines[0].split())
    edges = [list(map(int, line.split())) for line in lines[1:m + 1]]

    modified_edges = unify_mst(n, edges)
    return str(len(modified_edges))

