    n, m = map(int, stdin.readline().split())
    edges = [tuple(map(int, stdin.readline().split())) for _ in range(m)]

    def kruskal(n, m, edges):
        parent = list(range(n))
        rank = [0] * n
        edges.sort()

        def find(u):
            if u == parent[u]:
                return u
            parent[u] = find(parent[u])
            return parent[u]

        def union(u, v):
            u_root = find(u)
            v_root = find(v)
            if rank[u_root] > rank[v_root]:
                parent[v_root] = u_root
            elif rank[u_root] < rank[v_root]:
                parent[u_root] = v_root
            else:
                parent[v_root] = u_root
                rank[u_root] += 1

        spanning_tree = []
        for u, v in edges:
            if find(u) != find(v):
                union(u, v)
                spanning_tree.append((u, v))
        return spanning_tree

    def max_degree(n, m, edges):
        max_deg = 0
        for i in range(n):
            deg = len([j for j in edges if i in j])
            max_deg = max(max_deg, deg)
        return max_deg

    def max_deg_spanning_tree(n, m, edges):
        max_deg = max_degree(n, m, edges)
        trees = []
        for u, v in kruskal(n, m, edges):
            deg_u = len([e for e in edges if u in e])
            deg_v = len([e for e in edges if v in e])
            if deg_u == max_deg or deg_v == max_deg:
                trees.append((u, v))
        return trees

    result = max_deg_spanning_tree(n, m, edges)
    return '\n'.join(f'{u} {v}' for u, v in result)

