        for p in points:
            max_level = max(max_level, max(p))
        return max_level

    def closest_point(points, level):
        level_points = [p for p in points if max(p) == level]
        min_distance = float('inf')
        closest_point = None
        for p in level_points:
            dist = distance((0, 0), p)
            if dist < min_distance:
                min_distance = dist
                closest_point = p
        return closest_point

