        if current == target:
            return total_distance
        if level > max_level(points):
            return float('inf')

        level_points = [p for p in points if max(p) == level]
        for p in level_points:
            if p not in visited:
                visited.add(p)
                total_distance += distance(current, p)
                result = dfs(points, visited, p, target, level + 1, total_distance)
                if result != float('inf'):
                    return result
                total_distance -= distance(current, p)
                visited.remove(p)
        return float('inf')

