        for i in range(1, n):
            if is_round(i) and is_round(n - i):
                min_summands = [i, n - i]
                break
        return min_summands

    def find_min_summands_recursive(n, round_nums):
        min_summands = []
        for round_num in round_nums:
            if round_num <= n:
                new_num = n - round_num
                if is_round(new_num):
                    min_summands.append(round_num)
                    if new_num != round_num:
                        min_summands.append(new_num)
                else:
                    new_round_nums = round_numbers(new_num)
                    recursive_min_summands = find_min_summands_recursive(new_num, new_round_nums)
                    if recursive_min_summands:
                        min_summands.extend(recursive_min_summands)
                    if min_summands:
                        min_summands.append(round_num)
                if min_summands:
                    break
        return min_summands

