    def min_shots(a: List[int]) -> Tuple[int, List[int]]:
        n = len(a)
        dp = [0] * n
        parent = [[0] * n for _ in range(n)]

        for i in range(n):
            dp[i] = i * a[i]
            parent[i][i] = i

        for l in range(2, n + 1):
            for i in range(n - l + 1):
                j = i + l - 1
                dp[i][j] = float('inf')
                for k in range(i, j):
                    shots = dp[i][k] + dp[k + 1][j] + (j - i + 1) * (a[i] * k + 1)
                    if shots < dp[i][j]:
                        dp[i][j] = shots
                        parent[i][j] = k

        return dp[0][n - 1], parent[0][n - 1]

