    can_paint = False

    for i in range(H):
        for j in range(W):
            if grid[i][j] == '#':
                can_paint = True
                break
        if can_paint:
            break

    if not can_paint:
        return "No"

    def dfs(i: int, j: int, visited: set) -> bool:
        if (i, j) in visited:
            return True
        if not (0 <= i < H and 0 <= j < W and grid[i][j] == '#'):
            return False
        visited.add((i, j))
        for di, dj in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            if dfs(i + di, j + dj, visited):
                return True
        return False

    for i in range(H):
        for j in range(W):
            if grid[i][j] == '#' and not dfs(i, j, set()):
                return "No"

    return "Yes"

