def solution(stdin: str) -> str:
    def find(parent, i):
        if parent[i] == i:
            return i
        return find(parent, parent[i])

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1

    def is_connected(parent, x, y):
        return find(parent, x) == find(parent, y)

    input_list = list(map(int, stdin.split()))
    N, M = input_list[0], input_list[1]
    edges = input_list[2:]

    parent = [i for i in range(N + 1)]
    rank = [0] * (N + 1)

    for i in range(0, M, 2):
        union(parent, rank, edges[i], edges[i + 1])

    bridge_count = 0
    for i in range(0, M, 2):
        temp = parent[edges[i]]
        union(parent, rank, edges[i], edges[i + 1])
        if temp != parent[edges[i]]:
            bridge_count += 1

    return str(bridge_count)

