    def is_lexicographically_less(p: List[int], q: List[int]) -> bool:
        n = len(p)
        for i in range(n):
            if p[i] != q[i]:
                return p[i] < q[i]
        return False

    def swap_elements(permutation: List[int], i: int) -> List[int]:
        permutation[i], permutation[i + 1] = permutation[i + 1], permutation[i]
        return permutation

    def find_minimum_permutation(permutation: List[int]) -> List[int]:
        n = len(permutation)
        for i in range(n - 1, 0, -1):
            for j in range(i, 0, -1):
                if is_lexicographically_less(permutation, swap_elements(permutation, j - 1)):
                    permutation = swap_elements(permutation, j - 1)
                    break
        return permutation

