            i = 0
            while i < len(non_zero_numbers):
                if i + 1 < len(non_zero_numbers) and non_zero_numbers[i] == non_zero_numbers[i + 1]:
                    merged_numbers.append(2 * non_zero_numbers[i])
                    i += 2
                else:
                    merged_numbers.append(non_zero_numbers[i])
                    i += 1
            merged_numbers += [0] * (len(row) - len(merged_numbers))
            return merged_numbers

        def move_left_row(row: list) -> list:
            merged_row = merge_left(row)
            return merged_row

        moved_grid = [move_left_row(row) for row in grid]
        return moved_grid

    def move_up(grid: list) -> list:
        rotated_grid = [list(row) for row in zip(*grid)]
        moved_rotated_grid = move_left(rotated_grid)
        moved_grid = [list(row) for row in zip(*moved_rotated_grid)]
        return moved_grid

    def move_right(grid: list) -> list:
        def move_right_row(row: list) -> list:
            return row[::-1] + [0] * (len(row) - len(row.lstrip([0])))

        moved_grid = [move_right_row(row) for row in grid]
        return moved_grid

    def move_down(grid: list) -> list:
        rotated_grid = [list(row) for row in zip(*grid)]
        rotated_moved_grid = move_right(rotated_grid)
        moved_grid = [list(row) for row in zip(*rotated_moved_grid)]
        return moved_grid

    lines = stdin.strip().split('\n')
    grid = [[int(num) for num in line.strip().split(' ')] for line in lines[:-1]]
    direction = int(lines[-1])

