    i = 0
    while i < len(input_molecule):
        if input_molecule[i].isupper():
            atom = input_molecule[i]
            i += 1
            if i < len(input_molecule) and input_molecule[i].isdigit():
                count = int(input_molecule[i])
                i += 1
            else:
                count = 1
            if atom in input_dict:
                input_dict[atom] += count
            else:
                input_dict[atom] = count
        else:
            i += 1

    # Parse desired output into a dictionary of atoms and their counts
    output_dict = {}
    i = 0
    while i < len(desired_output):
        if desired_output[i].isupper():
            atom = desired_output[i]
            i += 1
            if i < len(desired_output) and desired_output[i].isdigit():
                count = int(desired_output[i])
                i += 1
            else:
                count = 1
            if atom in output_dict:
                output_dict[atom] += count
            else:
                output_dict[atom] = count
        else:
            i += 1

    # Check if the desired output is possible to create
    possible = True
    for atom, count in output_dict.items():
        if atom not in input_dict or input_dict[atom] // k < count:
            possible = False
            break

