        return sum(c1 != c2 for c1, c2 in zip(s1, s2))

    def most_likely_tree(sequences):
        n = len(sequences)
        dp = [[0] * n for _ in range(n)]
        edges = [[] for _ in range(n)]

        for i in range(n):
            dp[i][i] = 0

        for length in range(1, n):
            for i in range(n - length):
                j = i + length
                dp[i][j] = float('inf')
                for k in range(i, j):
                    d = dp[i][k] + dp[k + 1][j] + hamming_distance(sequences[i], sequences[j])
                    if d < dp[i][j]:
                        dp[i][j] = d
                        edges[i].append(j)

        return dp, edges

    lines = stdin.splitlines()
    n, k = map(int, lines[0].split())
    sequences = lines[1:]

