    t, n, m, grid = int(stdin.split('\n')[0].split()[0]), int(stdin.split('\n')[0].split()[1]), int(stdin.split('\n')[0].split()[2]), [list(line.strip()) for line in stdin.split('\n')[1:]]

    def is_safe(x, y):
        return 0 <= x < n and 0 <= y < m and grid[x][y] != '1'

    def bfs(start_x, start_y):
        queue = [(start_x, start_y)]
        visited = set()
        while queue:
            x, y = queue.pop(0)
            if (x, y) in visited:
                continue
            visited.add((x, y))

            if (x, y) == (0, 0) or (x, y) == (n - 1, m - 1) or (x, y) == (n - 1, 0) or (x, y) == (0, m - 1):
                return len(visited)

            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                new_x, new_y = x + dx, y + dy
                if is_safe(new_x, new_y):
                    if grid[new_x][new_y] in {'R', 'L', 'U', 'D'} and (new_x, new_y) not in visited and (new_x - dx, new_y - dy) in visited:
                        continue
                    queue.append((new_x, new_y))
        return None

    start_x, start_y = None, None
    for x in range(n):
        for y in range(m):
            if grid[x][y] == 'S':
                start_x, start_y = x, y
                break
        if start_x is not None and start_y is not None:
            break

    if start_x is None or start_y is None:
        return "NOT POSSIBLE"

    result = bfs(start_x, start_y)
    if result is None:
        return "NOT POSSIBLE"
    else:
        return str(result)

