        area = (s * (s - A) * (s - B) * (s - C)) ** 0.5
        if area == 0:
            return 0
        else:
            h = 2 * area / A
            return h + A

    max_length = 0
    for A, B, C in triangles:
        max_length = max(max_length, get_max_length(A, B, C))

    return f"{max_length:.4f}"

