    distance_matrix = []
    for i in range(n):
        distance_matrix.append(list(map(int, stdin.readline().split())))

    def find_closest_node(visited, dist):
        min_dist = float('inf')
        closest_node = None
        for i in range(n):
            if not visited[i] and dist[i] < min_dist:
                min_dist = dist[i]
                closest_node = i
        return closest_node

    def dijkstra(start):
        dist = [float('inf')] * n
        dist[start] = 0
        visited = [False] * n
        while not all(visited):
            node = find_closest_node(visited, dist)
            if node is None:
                break
            visited[node] = True
            for i in range(n):
                if not visited[i] and distance_matrix[node][i] != -1 and dist[node] + distance_matrix[node][i] < dist[i]:
                    dist[i] = dist[node] + distance_matrix[node][i]
                    distance_matrix[i][node] = distance_matrix[node][i]
        return dist

    start = 0
    dist = dijkstra(start)

    result = []
    for i in range(n - 1):
        for j in range(i + 1, n):
            if distance_matrix[i][j] != -1:
                result.append((i + 1, j + 1))

    return '\n'.join([' '.join(map(str, edge)) for edge in result]) + '\n'

