    def rotate_string(s: str, rotation: int) -> str:
        return ''.join([chr(((ord(ch) - 65 + rotation) % 26) + 65) for ch in s])

    def merge_strings(s1: str, s2: str) -> str:
        return ''.join([rotate_string(s1[i], ord(s2[i]) - 65) for i in range(len(s1))])

    def decrypt(drm_message: str) -> str:
        half_length = len(drm_message) // 2
        first_half = drm_message[:half_length]
        second_half = drm_message[half_length:]

        first_rotation = sum(ord(ch) - 64 for ch in first_half)
        second_rotation = sum(ord(ch) - 64 for ch in second_half)

        rotated_first_half = rotate_string(first_half, first_rotation)
        rotated_second_half = rotate_string(second_half, second_rotation)

        return merge_strings(rotated_first_half, rotated_second_half)

    drm_message = stdin.readline().strip()
    decrypted_message = decrypt(drm_message)
    return decrypted_message

