    def is_possible(n: int, d: list) -> bool:
        if n == 1:
            return True
        visited = [False] * n
        stack = [0]
        while stack:
            curr = stack.pop()
            if not visited[curr]:
                visited[curr] = True
                if d[curr] == 0:
                    continue
                for i in range(curr + 1, curr + d[curr] + 1):
                    if i < n and not visited[i] and d[i] > 0:
                        stack.append(i)
        return is_increasing(list(i for i in range(n) if visited[i]))

