        return 1 <= r <= N and 1 <= c <= N

    def bfs(board, knight_pos):
        visited = set()
        queue = [(knight_pos, 0)]

        while queue:
            (r, c), steps = queue.pop(0)
            visited.add((r, c))

            if r == 1 and c == 1:
                return steps

            for dr, dc in [(-2, 1), (-2, -1), (2, 1), (2, -1), (-1, 2), (-1, -2), (1, 2), (1, -2)]:
                new_pos = (r + dr, c + dc)
                if is_valid(*new_pos) and board[new_pos[0] - 1][new_pos[1] - 1] != '#' and new_pos not in visited:
                    queue.append((new_pos, steps + 1))

        return -1

    knight_pos = None
    for r in range(N):
        for c in range(N):
            if board[r][c] == 'K':
                knight_pos = (r + 1, c + 1)
                break

    if knight_pos is None:
        return -1

    return bfs(board, knight_pos)

