def solution(stdin: str) -> str:
    # Create the Playfair cipher table with the given key phrase
    key_phrase = stdin.split('\n')[0]
    key_phrase = key_phrase.replace('q', '').replace('Q', '')
    key_phrase = ''.join(sorted(set(key_phrase), key=key_phrase.index))
    table = [[''] * 5 for _ in range(5)]
    for i in range(5):
        for j in range(5):
            if i * 5 + j < len(key_phrase):
                table[i][j] = key_phrase[i * 5 + j]
            else:
                for k in range(26):
                    if chr(k + ord('A')) not in key_phrase and chr(k + ord('a')) not in key_phrase:
                        table[i][j] = chr(k + ord('A'))
                        break
    # Encrypt the plaintext
    plaintext = stdin.split('\n')[1]
    plaintext = plaintext.replace(' ', '').upper()
    ciphertext = ''
    i = 0
    while i < len(plaintext):
        if i == len(plaintext) - 1 or plaintext[i] == plaintext[i + 1]:
            plaintext = plaintext[:i + 1] + 'X' + plaintext[i + 1:]
        elif i < len(plaintext) - 1 and plaintext[i:i + 2] == 'X':
            plaintext = plaintext[:i + 1] + 'X' + plaintext[i + 1:]
        else:
            digraph = plaintext[i:i + 2]
            row1, col1 = -1, -1
            row2, col2 = -1, -1
            for j in range(5):
                if digraph[0] in table[j]:
                    row1, col1 = j, table[j].index(digraph[0])
                if digraph[1] in table[j]:
                    row2, col2 = j, table[j].index(digraph[1])
            if row1 == row2:
                ciphertext += table[row1][(col1 + 1) % 5] + table[row2][(col2 + 1) % 5]
            elif col1 == col2:
                ciphertext += table[(row1 + 1) % 5][col1] + table[(row2 + 1) % 5][col2]
            else:
                ciphertext += table[row1][col2] + table[row2][col1]
            i += 2
    return ciphertext

