    def process_buy(x, y):
        total_shares += x
        total_cost += x * y

    def process_sell(x, y):
        profit = (y - average_cost) * x
        tax = profit * 0.3
        total_profit += profit - tax
        total_shares -= x
        total_cost -= x * average_cost

    def process_split(x):
        total_shares *= x
        total_cost /= x

    def process_merge(x):
        if total_shares % x == 0:
            total_shares //= x
            total_cost *= x
        else:
            remainder = total_shares % x
            process_sell(remainder, average_cost)
            total_shares //= x
            total_cost *= x

    def process_die(y):
        final_profit = (y - average_cost) * total_shares
        tax = final_profit * 0.3
        return final_profit - tax

    stdin = stdin.strip().split('\n')
    total_shares = 0
    total_cost = 0
    total_profit = 0

    for line in stdin:
        command, *args = line.split()
        args = [int(arg) for arg in args]

        if command == 'buy':
            process_buy(*args)
        elif command == 'sell':
            process_sell(*args)
        elif command == 'split':
            process_split(*args)
        elif command == 'merge':
            process_merge(*args)
        elif command == 'die':
            total_profit += process_die(*args)

    return f"{total_profit:.8f}"

