def solution(stdin: str) -> str:
    n = int(stdin.splitlines()[0])
    a = list(map(int, stdin.splitlines()[1].split()))

    def longest_increasing_subsequence(a: list) -> int:
        def binary_search(tails: list, key: int) -> int:
            left, right = 0, len(tails) - 1
            while left <= right:
                mid = (left + right) // 2
                if tails[mid] == key:
                    return mid
                elif tails[mid] < key:
                    left = mid + 1
                else:
                    right = mid - 1
            return left

        tails = []
        for x in a:
            i = binary_search(tails, x)
            if i == len(tails):
                tails.append(x)
            else:
                tails[i] = x
        return len(tails)

    k = longest_increasing_subsequence(a)
    s = ""
    for i in range(k):
        s += "L" if i % 2 == 0 else "R"
    return str(k) + "\n" + s

