def solution(stdin: str) -> str:
    n = int(stdin)
    count = 1  # include the number itself

    def add_one(num: int) -> int:
        return num + 1

    def remove_trailing_zeroes(num: int) -> int:
        while num > 1 and num % 10 == 0:
            num //= 10
        return num

    def is_reachable(num: int, limit: int) -> bool:
        current_num = num
        for _ in range(limit):
            current_num = remove_trailing_zeroes(add_one(current_num))
            if current_num == 1:
                return True
        return False

    reachable_count = 0
    limit = 0
    while not is_reachable(n, limit):
        count += limit + 1
        reachable_count += limit + 1
        limit += 1

    count += reachable_count
    return str(count)

