def solution(stdin: str) -> str:
    input_list = stdin.split('\n')
    n, m, D = map(int, input_list.pop(0).split())
    adj_list = [[] for _ in range(n)]
    for _ in range(m):
        u, v = map(int, input_list.pop(0).split())
        adj_list[u-1].append(v-1)
        adj_list[v-1].append(u-1)
    visited = [False] * n
    dfs = [False] * n
    result = []

    def dfs_visit(node):
        visited[node] = True
        for neighbor in adj_list[node]:
            if not visited[neighbor]:
                dfs[neighbor] = True
                dfs_visit(neighbor)

    def dfs_clear():
        for i in range(n):
            dfs[i] = False

    def add_edge(u, v):
        result.append((u+1, v+1))

    dfs_visit(0)
    visited_count = sum(visited)
    if visited_count != D:
        return "NO\n"

    for i in range(n):
        if visited[i]:
            for j in adj_list[i]:
                if dfs[j]:
                    add_edge(i, j)

    dfs_clear()
    for i in range(n):
        if visited[i]:
            dfs_visit(i)

            if sum(dfs) == D:
                add_edge(0, i)
                for j in adj_list[i]:
                    if visited[j] and not dfs[j]:
                        add_edge(i, j)
                break

    if sum(visited) == D:
        return "YES\n" + '\n'.join(str(e) for e in result)
    else:
        return "NO\n"

