    def length_of_intersection(segments):
        intersection = []
        for i in range(len(segments) - 1):
            for j in range(i + 1, len(segments)):
                x1, x2 = sorted([segments[i][0], segments[j][0]])
                y1, y2 = sorted([segments[i][1], segments[j][1]])
                intersection.extend(range(max(x1, y1), min(x2, y2) + 1))
        return len(set(intersection))

    max_length = 0
    for i in range(len(segments)):
        current_length = length_of_intersection(segments[:i] + segments[i + 1:])
        max_length = max(max_length, current_length)

    return str(max_length)

