    n = int(stdin)
    nums = list(map(int, stdin[n+1:].split()))
    matrix = [[0]*n for _ in range(n)]

    def is_palindrome(matrix):
        for i in range(n):
            for j in range(n):
                if matrix[i][j] != matrix[n-1-i][n-1-j]:
                    return False
        return True

    def backtrack(nums, row, col):
        if col == n:
            return is_palindrome(matrix)
        for i in range(n):
            if nums[i] == matrix[row][col]:
                continue
            matrix[row][col] = nums[i]
            if backtrack(nums, row, col+1):
                return True
            matrix[row][col] = 0
        return False

    def is_valid(nums, row, col):
        return all(nums[i] != matrix[row][col] for i in range(n))

    def fill_matrix(nums, row, col):
        if col == n:
            return is_palindrome(matrix)
        for i in range(n):
            if not is_valid(nums, row, i):
                continue
            matrix[row][i] = nums[i]
            if fill_matrix(nums, row+1, col):
                return True
            matrix[row][i] = 0
        return False

    if fill_matrix(nums, 0, 0):
        for row in matrix:
            print(' '.join(map(str, row)))
        return "YES"
    else:
        return "NO"

