def solution(stdin: str) -> str:
    n, m, d = map(int, stdin.split()[:3])
    c = list(map(int, stdin.split()[3:]))

    def is_valid_move(x, y):
        if x < 0 or x > n or y < 0 or y > n:
            return False
        return True

    def is_valid_platform(x, c):
        return is_valid_move(x, x + c)

    def is_valid_jump(x, c):
        for i in range(x + 1, x + d + 1):
            if is_valid_move(i, i + c):
                return True
        return False

    def build_platform_sequence(x):
        sequence = [0] * n
        for i in range(1, m + 1):
            c = c[i - 1]
            while c > 0 and is_valid_platform(x, c):
                sequence[x] = i
                x += 1
                c -= 1
        return sequence

    def build_jump_sequence(x):
        sequence = [0] * n
        for i in range(1, m + 1):
            c = c[i - 1]
            while c > 0 and is_valid_jump(x, c):
                sequence[x] = i
                for j in range(x + 1, x + d + 1):
                    if sequence[j] == 0 and is_valid_move(j, j + c):
                        sequence[j] = i
                x += 1
                c -= 1
        return sequence

    if is_valid_move(0, n + 1):
        sequence = build_platform_sequence(0)
        if sequence[-1] != m:
            sequence = build_jump_sequence(0)
            if sequence[-1] != m:
                return "NO"
    else:
        return "NO"

    return "YES\n" + " ".join(map(str, sequence))

