        if len(arr) <= 1:
            return arr
        mid = len(arr) // 2
        left = merge_sort(arr[:mid])
        right = merge_sort(arr[mid:])
        return merge(left, right)

    def merge(left, right):
        result = []
        i = j = 0
        while i < len(left) and j < len(right):
            if left[i] < right[j]:
                result.append(left[i])
                i += 1
            else:
                result.append(right[j])
                j += 1
        result.extend(left[i:])
        result.extend(right[j:])
        return result

    def distribute_coins(coins):
        sorted_coins = merge_sort(coins)
        pockets = [[]]
        for coin in sorted_coins:
            found_pocket = False
            for pocket in pockets:
                if not pocket or pocket[-1] != coin:
                    pocket.append(coin)
                    found_pocket = True
                    break
            if not found_pocket:
                pockets.append([coin])
        return len(pockets)

    n = int(stdin.splitlines()[0])
    coins = list(map(int, stdin.splitlines()[1].split()))
    result = distribute_coins(coins)
    return str(result)

