    def get_sum(i, j):
        return (a[i] + b[j]) % n

    def is_less_lexicographically(x, y):
        for i in range(n):
            if x[i] < y[i]:
                return True
            elif x[i] > y[i]:
                return False
        return False

    min_b = b.copy()
    for i in range(n):
        for j in range(n):
            sum_a_b = get_sum(i, j)
            if sum_a_b < min_b[i]:
                min_b[i] = sum_a_b

    c = [min_b[i] for i in range(n)]
    while is_less_lexicographically(c, [0] * n):
        for i in range(n):
            c[i] += n
            if c[i] >= n:
                c[i] -= n

    return ' '.join(map(str, c))

