    def count_black_squares(rows, cols):
        nonlocal red_squares
        red_squares = 0
        for i in range(1, H + 1):
            for j in range(1, W + 1):
                if i in rows or j in cols:
                    red_squares += 1 if grid[i - 1][j - 1] == '#' else 0
                else:
                    red_squares += 1 if grid[i - 1][j - 1] == '#' else 0
        return red_squares

    def backtrack(rows, cols, choice, depth):
        if depth == 0:
            return 1 if count_black_squares(rows, cols) == K else 0
        res = 0
        for i in range(1, H + 1):
            for j in range(1, W + 1):
                if (i not in rows and j not in cols) or (i in rows and j in cols):
                    res += backtrack(rows | {i}, cols | {j}, choice and False, depth - 1)
        return res

    return str(backtrack(set(), set(), True, K))


