def solution(stdin: str) -> str:
    def is_valid_block(l, r, used_indices):
        for index in range(l, r + 1):
            if index in used_indices:
                return False
        return True

    def find_blocks(l, r, used_indices):
        if not is_valid_block(l, r, used_indices):
            return []

        if l == r:
            return [(l, r)]

        result = []
        for i in range(l, r + 1):
            if i - l > 1 and (i - l) % 2 == 0:
                result += find_blocks(l, i - 1, used_indices)
                result += find_blocks(i + 1, r, used_indices | {i})
            elif i - l == 1:
                result += find_blocks(l, i, used_indices)
                result += find_blocks(i + 1, r, used_indices)

        return result

    n, *a = list(map(int, stdin.split()))

    max_blocks = 0
    max_blocks_set = []

    for i in range(n):
        for j in range(i + 1, n + 1):
            if j - i == 1:
                blocks = [(i, j)]
            elif j - i == 2:
                blocks = [(i, i + 1), (i + 1, j)]
            else:
                blocks = find_blocks(i, j, set())

            if len(blocks) > max_blocks:
                max_blocks = len(blocks)
                max_blocks_set = blocks

    output = '\n'.join(f"{l} {r}" for l, r in max_blocks_set)
    return f"{max_blocks}\n{output}"

