def solution(stdin: str) -> str:
    input_str = stdin.strip()
    input_list = input_str.split("\n")
    n, m = map(int, input_list[0].split())
    grid = [list(x) for x in input_list[1:]]
    stars = []
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '*':
                stars.append((i, j, 1))
    if len(stars) > n * m:
        return "-1"

    # find positions of stars and check if they are valid
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '*':
                continue
            star_candidates = []
            for s in range(1, n * m - len(stars) + 1):
                for si in range(i - s + 1, i + s):
                    for sj in range(j - s + 1, j + s):
                        if si < 0 or si >= n or sj < 0 or sj >= m:
                            continue
                        if grid[si][sj] == '*':
                            continue
                        star_candidates.append((si, sj, s))
                if len(star_candidates) >= 1:
                    stars.append(star_candidates[0])
                    break
            if len(stars) > n * m:
                return "-1"

    if len(stars) <= n * m:
        return str(len(stars)) + "\n" + "\n".join([" ".join(map(str, star)) for star in stars])
    else:
        return "-1"

