        visited.add(start)
        for i, (u, v) in enumerate(edges):
            if u == start:
                if v not in visited and dfs(v, visited):
                    return True
            elif v == start:
                if u not in visited and dfs(u, visited):
                    return True
        return False

    for i in range(1, n+1):
        if not dfs(i, set()):
            return "NO"
    return "YES"


