def solution(stdin: str) -> str:
    def find(parent, node):
        if parent[node] != node:
            parent[node] = find(parent, parent[node])
        return parent[node]

    def union(parent, rank, x, y):
        xroot, yroot = find(parent, x), find(parent, y)
        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1

    def kruskal(n, edges):
        edges.sort(key=lambda x: x[2])
        parent = [i for i in range(n + 1)]
        rank = [0] * (n + 1)
        mst_cost = 0
        for u, v, w in edges:
            if find(parent, u) != find(parent, v):
                union(parent, rank, u, v)
                mst_cost += w
                if len(set(find(parent, i) for i in range(1, n + 1))) == 1:
                    return mst_cost
        return -1

    n, m = map(int, stdin.split()[:2])
    edges = [tuple(map(int, stdin.split()[i:i + 3])) for i in range(2, 2 * m + 1, 3)]

    mst_cost = kruskal(n, edges)
    if mst_cost == -1:
        return 0

    operations = 0
    for i in range(m):
        u, v, w = edges[i]
        for j in range(i + 1, m):
            x, y, z = edges[j]
            if w == z:
                continue
            if (u, v, w + 1) == (x, y, z) or (u, v, w + 1) == (y, x, z) or (v, u, w + 1) == (x, y, z) or (v, u, w + 1) == (y, x, z):
                operations += 1
                break
        if operations >= 2:
            break

    return operations

