    def is_nice(garland):
        for i in range(n):
            for j in range(i + 1, n):
                if garland[i] == garland[j] and (j - i) % 3 != 0:
                    return False
        return True

    def recolor(garland, k):
        for i in range(k, n):
            if garland[i] == 0:
                garland[i] = 2
            elif garland[i] == 2:
                garland[i] = 1
            else:
                garland[i] = 0
        return garland

