    edges = []
    for i in range(m):
        edges.append(tuple(map(int, stdin.split()[2*i+2:2*i+4])))

    # Find the edges that are part of a spanning tree
    def find_spanning_tree(edges):
        visited = [False] * n
        result = []
        for i in range(n):
            if not visited[i]:
                visited[i] = True
                result.append((i, i))
                for j in range(len(edges)):
                    if edges[j][0] == i or edges[j][1] == i:
                        result.append(edges[j])
                        visited[edges[j][0]] = visited[edges[j][1]] = True
        return result

    # Find the maximum degree over all vertices in the spanning tree
    def max_degree(edges):
        max_degree = 0
        for i in range(n):
            degree = sum(1 for edge in edges if edge[0] == i or edge[1] == i)
            max_degree = max(max_degree, degree)
        return max_degree

    # Find the edges that have the maximum degree over all vertices
    spanning_tree = find_spanning_tree(edges)
    max_degree_tree = [edge for edge in spanning_tree if max_degree(spanning_tree) == sum(1 for e in spanning_tree if e[0] == edge[0] or e[1] == edge[0]) + sum(1 for e in spanning_tree if e[0] == edge[1] or e[1] == edge[1])]

    # Print the edges of the spanning tree with maximum degree
    print('\n'.join([' '.join(map(str, edge)) for edge in max_degree_tree]))

