    def distance(p1, p2):
        return abs(p1[0] - p2[0]) + abs(p1[1] - p2[1])

    def bfs(graph, start):
        visited = set()
        queue = [(start, 0)]
        while queue:
            (vertex, level) = queue.pop(0)
            if vertex not in visited:
                visited.add(vertex)
                for neighbor in graph[vertex]:
                    queue.append((neighbor, level + 1))
        return visited

    def find_key_points(n, points):
        graph = {i: [] for i in range(n)}
        for i in range(n):
            for j in range(i + 1, n):
                if max(distance(points[i], points[j]), distance(points[j], points[i])) <= 2:
                    graph[i].append(j)
                    graph[j].append(i)
        return graph

    def min_distance(n, points):
        graph = find_key_points(n, points)
        visited = set()
        min_dist = float('inf')
        for i in range(n):
            if i not in visited:
                visited.add(i)
                level_points = bfs(graph, i)
                for p in level_points:
                    visited.add(p)
                dist = sum(distance(points[i], points[j]) for j in level_points)
                min_dist = min(min_dist, dist)
        return min_dist

    n, *points = stdin.split('\n')
    n = int(n)
    points = [tuple(map(int, point.split())) for point in points]
    result = min_distance(n, points)
    return str(result)

