    edges = [tuple(map(int, line.split())) for line in stdin.split('\n')[1:m+1]]
    adj_matrix = [[float('inf') for _ in range(n)] for _ in range(n)]
    for x, y, w in edges:
        adj_matrix[x-1][y-1] = w
        adj_matrix[y-1][x-1] = w

    # Dijkstra's algorithm
    for i in range(n):
        for j in range(n):
            if i == j:
                adj_matrix[i][j] = 0
            elif adj_matrix[i][j] == float('inf'):
                adj_matrix[i][j] = adj_matrix[j][i]

    for i in range(n):
        for j in range(n):
            for v in range(n):
                adj_matrix[i][j] = min(adj_matrix[i][j], adj_matrix[i][v] + adj_matrix[v][j])

    # Sort all the shortest paths
    shortest_paths = []
    for i in range(n):
        for j in range(n):
            if i < j:
                shortest_paths.append(adj_matrix[i][j])
    shortest_paths.sort()

    # Find the k-th smallest path
    kth_path = shortest_paths[k-1]

    return kth_path

